import React, { useEffect, useState } from 'react';
import classes from './DetectPangram.module.css';


const DetectPangram = () => {
    const [currentValue, setCurrentValue] = useState('');

    useEffect(() => { console.log(currentValue) }, [currentValue])

    const onChangeStringHandle = (eve) => setCurrentValue(eve.target.value);

    return (
        <div>
            <h1>DETECT PANGRAM</h1>
            <p>
                A pangram is a sentence that contains every single letter of the alphabet at least once. For example, the sentence "The quick brown fox jumps over the lazy dog" is a pangram, because it uses the letters A-Z at least once (case is irrelevant).

                Given a string, detect whether or not it is a pangram. Return True if it is, False if not. Ignore numbers and punctuation.
            </p>
            <label>
                <input
                    value={currentValue}
                    onChange={onChangeStringHandle}
                />
            </label>
            <button>check a string</button>
        </div >
    )
}

export default DetectPangram;
