import './App.css';
import { useState } from "react";

function App() {
  const initialState = {
    optionArray: [
      '1',
      '2',
    ],
  };

  const [state, setState] = useState(initialState);
  return (
    <div className='appWrapper'>
      <label>
        {state.optionArray.map(el => <option> ${el} </option>)}
      </label>
    </div>
  );
}

export default App;


