import './App.css';
import { useEffect, useState } from "react";
import DetectPangram from './components/DetectPangram/DetectPangram';

function App() {
  const initialState = {
    optionArray: [
      'Detect Pangram',
      'Detect Pangram 2',
    ],
    currentComponent: 'Detect Pangram'
  };

  const [state, setState] = useState(initialState);

  // useEffect(() => console.log(state), [state]);

  const onChangeHandle = (eve) => {
    setState(prevState => {
      return {
        ...prevState,
        currentComponent: eve.target.value
      }
    })
  }

  let drawCurrentComponent;

  switch (state.currentComponent) {
    case 'Detect Pangram':
      drawCurrentComponent = < DetectPangram />;
      break;
    case 'Detect Pangram 2':
      drawCurrentComponent = < DetectPangram />;
      break;
    default:
      alert('Houston we have a problem')
  }

  return (<div
    className='appWrapper'
  >
    <select
      onChange={onChangeHandle}
      value={state.currentComponent} > {
        state.optionArray.map((el, index) =>
          <option
            value={el}
            key={index}
          >
            {el}
          </option>)
      }
    </select>
    {drawCurrentComponent}
  </div>
  );
}

export default App;
