import './App.css';
import { useState } from "react";

function App() {
  const initialState = {
    optionArray: [
      'Detect Pangram',
      'Detect Pangram 2',
    ],
    currentComponentName: 'Detect Pangram'
  };

  const [state, setState] = useState(initialState);

  return (
    <div className='appWrapper'>
      <select>
        {state.optionArray.map((el, index) => <option key={index} > {el} </option>)}
      </select>
    </div>
  );
}

export default App;


